    "time": "2020-03-31T16:21:17.528002Z",
    "tmst": 3512348611,
    "chan": 2,
    "rfch": 0,
    "freq": 866.349812,
    "stat": 1,
    "modu": "LORA",
    "datr": "SF7BW125",
    "codr": "4/6",
    "rssi": -35,
    "lsnr": 5.1,
    "size": 32,
    "data": "-DS4CGaDCdG+48eJNM3Vai-zDpsR71Pn9CPA9uCON84",
    "ack": false,
    "adr": true,
    "mhdr": "4006000000000100",
    "opts": "",
    "port": 1,
    "seqn": 1,
    "fcnt": 1456,
    "cls": "",
    "deveui": "00-80-00-00-00-00-e1-9a",
    "appeui": "00-88-88-88-00-00-e1-9b",
    "gweui": "00-80-00-00-00-00-e1-9c"
}
*/

let feeds = {
    // deviceEUI: "",                                     // mandatory
    // time: "",
    // coordinates: [],                                   // [longitude, latitude, altitude]
                                                          // used if NS provides resolved coordinates
    solverInput: {
        solverInputType: "FeedInputForGpsSolver",
        // sequenceNumber: 1,                             // LoRaWAN FCntUp
        // port: 1,                                       // LoRaWAN FPort
        // receptionTime: "",
        // SF: 12,                                        // LoRaWAN Spreading Factor
        packets: [
            // {
                // baseStationId: "0805022D",
                // antennaId: 0,
                // antennaCoordinates: [7.0513, 43.6181], // [longitude, latitude, altitude]
                // antennaHeight: 150,                    // in cm
                // SNR: 10,                               // in dB
                // RSSI: -29,                             // in dBm
                // barometerMeasure: 0,                   // in mB
                // arrivalSeconds: 1,                     // in seconds
                // arrivalAdditionalNanos: 7275           // in nanoseconds
            // }
        ]
        // dynamicMotionState: "string",    // ['MOVING', 'STATIC', 'UNKNOWN']
        // temperatureMeasure: 0,           // in Celsius
        // accelerometerMeasures: [],       // measures per axis, i.e. [x, y, z]
        // gyroscopeMeasures: [],           // measures per axis, i.e. [roll, pitch, yaw]
        // barometerMeasure: 0,             // in mBar
        // lastContext: "string"            // Base64 encoded binary solver state
                                            // with the last calculated position
    },
    payload: {
        deviceProfileId: "ABEEWAY/MICRO",   // Currently only Abeeway Microtracker and
                                            // Industrial Tracker are supported. "deviceProfileId"
                                            // should be set to "ABEEWAY/MICRO" for both
        payloadEncoded: ""
    }
}

if ('deveui' in body) { 
    feeds.deviceEUI = body.deveui.replace(/-/g,"").toUpperCase(); 
}
else { 
    throw new Error("Missing property: deveui"); 
}

if ( 'time' in body ) { 
    feeds.time = body.time;
}

if ('seqn' in body) { 
    feeds.solverInput.sequenceNumber = body.seqn; 
}
if ('port' in body) { 
    feeds.solverInput.port = body.port 
}


if ('time' in body) { 
    feeds.solverInput.receptionTime = body.time; 
}
if ('datr' in body) { 
    feeds.solverInput.SF = parseInt(body.datr.substring(2,4)); // SF7BW125
}
let packet = {};
if ('gweui' in body) { 
    packet.baseStationId = body.gweui; 
} 
if ('lsnr'in body) { 
    packet.SNR = body.lsnr; 
}
if ('rssi' in body) { 
    packet.RSSI = body.rssi; 
}
feeds.solverInput.packets.push(packet);


if ('data' in body) { 
    feeds.payload.payloadEncoded = Buffer.from(body.data, 'base64').toString('hex'); 
}

msg.payload = feeds;

return msg;

/******************************************/
/*** Multitech DL interface              ***/
/******************************************/

let params = msg.payload;
msg.payload = {
    "deveui": params.deveui,
    "data": Buffer.from(params.payload, 'hex').toString('base64'),
    "port": params.port
};
msg.topic = "lora/" + params.deveui + "/down";
return msg;
